import React, { Component } from "react";
import "./HalDetails.css";
import { Link } from "react-router-dom";

class HalDetails extends Component {
  render() {
    let { hal } = this.props;
    const halPlan = {
      position: "absolute",
      width: hal.width,
      height: hal.height,
      top: hal.top,
      left: hal.left
    };

    return (
      <div>
        <div
          style={{
            position: "relative",
            height: 400
          }}
        >
          <div
            key={hal.ID}
            style={halPlan}
            className={`Hal ${this.props.help && "help"}`}
          >
            <h2>{hal.naam}</h2>
            <button onClick={this.props.onHelp} className="btn-danger">
              NOOD
            </button>
            <ul>
              {hal.apparaten.map(app => (
                <div
                  style={{
                    position: "absolute",
                    width: app.width,
                    height: app.height,
                    top: app.top,
                    left: app.left,
                    border: "3px solid #042a2b"
                  }}
                  key={app.ID}
                >
                  <ul>
                    <li>{app.naam}</li>
                    {app.categorie === "machine" && (
                      <Link to={`/${hal.naam}/apparaat/${app.ID}`}>
                        <i className="fas fa-cogs" />
                      </Link>
                    )}
                    {app.categorie === "lamp" && (
                      <Link to={`/${hal.naam}/apparaat/${app.ID}`}>
                        <i className="fas fa-lightbulb" />
                      </Link>
                    )}
                    {app.categorie === "band" && (
                      <Link to={`/${hal.naam}/apparaat/${app.ID}`}>
                        <i className="fas fa-dolly-flatbed" />
                      </Link>
                    )}
                  </ul>
                </div>
              ))}
            </ul>
          </div>
        </div>
        <Link to="/" className="btn btn-info">
          Go Back
        </Link>
      </div>
    );
  }
}

export default HalDetails;

