import React from 'react';
import './styles.css';
import Routes from './components/Routes/Routes';
import Header from './components/Header/Header';
import axios from 'axios';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      lijstView: true,
      isLoading: true,
      help: false,
      hallen: []
    };
    this.handleToggleClick = this.handleToggleClick.bind(this);
  }

  async componentDidMount() {
    const res = await axios.get('http://localhost:3000/hallen.json');
    let data = res.data.hallen;
    this.setState({
      isLoading: false,
      hallen: data
    });
  }

  handleToggleClick = () => {
    this.setState({
      lijstView: !this.state.lijstView
    });
  };

  handleHelp = () => {
    this.setState({
      help: !this.state.help
    });
  };

  render() {
    if (this.state.isLoading) {
      return (
        <div className='spinner'>
          <i className='fas fa-7x fa-spinner fa-spin' />
          <h1>Loading...</h1>
        </div>
      );
    }
    return (
      <div>
        <Header />
        <Routes
          hallen={this.state.hallen}
          lijst={this.state.lijstView}
          help={this.state.help}
          onClick={this.handleToggleClick}
          onHelp={this.handleHelp}
        />
      </div>
    );
  }
}

export default App;
